/**
 * The logo of the Renoun library.
 * @internal
 */
export function RenounMarkLink({
  size,
  className,
  style,
}: {
  size?: number
  className?: string
  style?: React.CSSProperties
}) {
  return (
    <a
      href="https://www.renoun.dev"
      rel="noopener"
      target="_blank"
      className={className}
      style={{ display: 'flex', ...style }}
    >
      <RenounMark size={size} />
    </a>
  )
}

