/**
 * A component that displays the Renoun logo that links to the Renoun website.
 * @internal
 */
export function RenounLogoLink({
  className,
  style,
}: {
  className?: string
  style?: React.CSSProperties
}) {
  return (
    <a
      href="https://www.renoun.dev"
      rel="noopener"
      target="_blank"
      className={className}
      style={{ display: 'flex', ...style }}
    >
      <RenounLogo style={{ width: undefined, height: '100%' }} />
    </a>
  )
}

/**
 * A component that displays a "Built with Renoun" message.
 * @internal
 */
export function BuiltWithRenoun({
  className,
  style,
}: {
  className?: string
  style?: React.CSSProperties
}) {
  return (
    <a
      href="https://www.renoun.dev"
      className={className}
      style={{
        display: 'flex',
        flexDirection: 'column',
        gap: '0.25lh',
        ...style,
      }}
    >
      <span>Built with</span>
      <RenounLogo />
    </a>
  )
}
